function toText(html){ return String(html||'').replace(/<[^>]+>/g,' ').replace(/\s{2,}/g,' ').trim(); }
function lower(x){ return String(x||'').toLowerCase(); }

const email   = $node["Email body cleaner"].json || {};
const tenant  = $node["Get tenant data"].json || {};
const history = Array.isArray($node["Get Conversation History"].json) ? $node["Get Conversation History"].json : [];
const lookup  = $node["Conversation Thread Lookup"].json || [];
const gen     = $node["Thread ID Generator"].json || [];

const rulesList = tenant.business_rules || [];
const rules = Object.fromEntries(rulesList.map(r => [r.rule_key, r.rule_config || {}]));
const templates = Object.fromEntries(rulesList.map(r => [r.rule_key, r.html_template || ""]));

// ---- Kernregels
const ladderCfg   = rules.compensatie_ladder || { stappen:[15,20,30,40], dreiging_percentage:50, onderhandeling_marge:15, max_totaal_normaal:40, max_totaal_dreiging:65, retour_na_weigering:true };
const weigering   = rules.weigering_detectie || { triggers:['nee','niet akkoord','ik wil retourneren','stuur adres','retour adres','ik wil terugsturen'], alleen_binnen_categorie:true };
const deliveryCfg = rules.levering_logica   || { drempel_1:5, drempel_2:9, normale_levertijd_werkdagen:'6-9' };

const annuleerMax = tenant.annulering_toegestaan_dagen ?? (rules.annulering_policy?.max_dagen_na_bestelling ?? 14);
const adresMax    = rules.adreswijziging_policy?.max_dagen_na_bestelling ?? 7;
const retourAdres = tenant.retouradres || "";

// teller
const dbTotal = Array.isArray(lookup) && lookup[0]?.total_interactions != null ? Number(lookup[0].total_interactions) : null;
const contactNumber = (dbTotal != null ? dbTotal + 1 : (gen.contact_count ?? 1));

// ladder-stap
const stepIndex   = Math.max(1, Math.min(contactNumber, ladderCfg.stappen.length));
const stepPercent = ladderCfg.stappen[stepIndex - 1] ?? 15;

// basis analyse
const body = lower(email.cleaned_body || email.email_body || "");
const subject = email.subject || "";

// dreiging?
const threatWords = (rules.dreiging_detectie?.trefwoorden || []).map(lower);
const isThreat = threatWords.some(kw => body.includes(kw));

// contextregels (laatste 5)
const last5 = history.slice(-5);
const contextLines = last5.map(h => {
  const ts = h.created_at || 'Unknown';
  const mb = toText(h.message_body || '');
  const ar = toText(h.ai_response  || '');
  const tp = (h.type || '').toLowerCase();
  const ls = h.ladder_stap != null ? ` (ladder:${h.ladder_stap})` : '';
  return `${ts} [${tp}] KLANT:"${mb}" | AI:"${ar}"${ls}`;
}).join("\n");

// lopend onderwerp?
const lastType = (history[0]?.type || '').toLowerCase();
const inCompensationFlow = ['compensation','return'].includes(lastType);

// weigering alleen binnen flow?
const refusalHit = (weigering.triggers || []).some(t => body.includes(lower(t)));
const treatAsRefusal = (!weigering.alleen_binnen_categorie ? refusalHit : (inCompensationFlow && refusalHit));

// klant vraagt expliciet hoger % ?
const askedPctMatch = body.match(/(\d{1,2})\s*%/);
const askedPct = askedPctMatch ? Math.min(100, Math.max(0, parseInt(askedPctMatch[1],10))) : null;
const maxExtra = ladderCfg.onderhandeling_marge ?? 15;

// levering signalen
const deliveryComplain = /(waar blijft|nog niet binnen|duurt lang|wacht al|levering|pakket)/i.test(body);
const generalLevertijdVraag = /(levertijd|hoelang|hoe lang)/i.test(body) && !deliveryComplain;

// bevestiging niet ontvangen
const confirmMiss = (rules.bevestiging_niet_ontvangen?.triggers || []).some(w => body.includes(lower(w)));

// order wijziging aangevraagd
const changeOrder = (rules.order_wijziging_aanvraag?.triggers || []).some(w => body.includes(lower(w)));

// "besteldatum" proxy: eerste contact in thread
function daysSinceFirstContact(){
  if (!history.length) return null;
  const oldest = history[history.length-1];
  try { return Math.floor((Date.now() - new Date(oldest.created_at).getTime()) / (1000*60*60*24)); }
  catch { return null; }
}
const days = daysSinceFirstContact();

// variatie
const openersNew   = ["Bedankt voor je bericht.","Fijn dat je even contact opneemt.","Dank je wel voor je melding."];
const openersFollow= ["Bedankt voor je snelle reactie.","Dank je voor je terugkoppeling.","Helder, dank voor je update."];
const closers      = ["Laat het me gerust weten als ik nog kan helpen.","Ik hoor graag of dit voor je werkt.","We denken graag met je mee."];

function pick(a){ return a[Math.floor(Math.random()*a.length)]; }
const opener = contactNumber > 1 ? pick(openersFollow) : pick(openersNew);
const closer = pick(closers);

const persona = tenant.ai_persona_name || 'Eva';
const bedrijf = tenant.bedrijfsnaam || 'onze winkel';

let prompt = `
Je bent ${persona}, professionele klantenservice van ${bedrijf}. Schrijf natuurlijk, vriendelijk Nederlands en geef **alleen HTML** (<p>...).
**HARD-REGELS:**
- Nooit proactief "retour" of "geld terug" aanbieden vóór maximale ladderstap is geweigerd.
- Ladder: ${ladderCfg.stappen.join('% → ')}%. Na weigering op ${ladderCfg.stappen[ladderCfg.stappen.length-1]}%: status "max_refused" en retouradres.
- Onderhandeling: als klant expliciet een hoger % vraagt, mag dat éénmalig tot +${maxExtra}% boven de geldende stap; max ${ladderCfg.max_totaal_normaal}% (dreiging: ${ladderCfg.max_totaal_dreiging}%).
- Dreiging (juridisch/politie): status "escalated", bied ${ladderCfg.dreiging_percentage}%. Bij weigering in vervolgstap: retouradres.
- Annulering toegestaan binnen ${annuleerMax} dagen na bestelling; anders uitleggen/doorvragen.
- Adreswijziging toegestaan binnen ${adresMax} dagen na bestelling; anders uitleggen/doorvragen.
- Levertijd (algemeen): antwoord kort met ${deliveryCfg.normale_levertijd_werkdagen} werkdagen.
- Levertijd-klacht (bestelling): vensters <${deliveryCfg.drempel_1} / ${deliveryCfg.drempel_1}-${deliveryCfg.drempel_2} / >${deliveryCfg.drempel_2} werkdagen.
- Variatie verplicht; geen dubbele sign-off; geen expliciete verwijzing naar aantal contactmomenten.

CONTEXT (laatste 5):
${contextLines || 'Geen eerdere gesprekken.'}

KLANT:
Onderwerp: ${subject}
Bericht: ${email.cleaned_body || email.email_body}

CONTACT:
- Dit is contactmoment: ${contactNumber}.
- Ladder-stap te hanteren: ${stepIndex} ⇒ ${stepPercent}% (tenzij onderhandeling expliciet hoger vraagt).

INTENTDETECTIE:
- Bepaal intentie: "general" | "delivery" | "cancellation" | "address_change" | "compensation" | "return" | "threat" | "confirmation_missing" | "change_order_request".
- Weigering/retour-triggers ALLEEN gebruiken als het gesprek al in "compensation/return" zit of klant expliciet retour wil.

LEVERTIJD-LOGICA:
- Als "delivery":
  * if days == null → neutraal geruststellen + vraag order-/trackinfo.
  * if days < ${deliveryCfg.drempel_1} → uitleg normale levertijd ${deliveryCfg.normale_levertijd_werkdagen}, vraag om geduld.
  * if ${deliveryCfg.drempel_1} ≤ days ≤ ${deliveryCfg.drempel_2} → empathie, “bijna geleverd”, vraag om track&trace/orderinfo.
  * if days > ${deliveryCfg.drempel_2} → excuses + vraag ordernummer, direct onderzoek; evt. klein % aanbieden.

JSON OUTPUT (één object):
{
  "ai_response": "<p>${opener}</p><p>...</p><p>${closer}</p>",
  "status": "processed" | "escalated" | "max_refused" | "accepted",
  "type": "general" | "delivery" | "cancellation" | "address_change" | "compensation" | "return" | "threat" | "confirmation_missing" | "change_order_request",
  "compensatie_percentage": ${isThreat ? ladderCfg.dreiging_percentage : stepPercent},
  "ladder_stap": ${stepIndex},
  "escalation_reason": null,
  "mood_detected": "neutral",
  "confidence_score": 0.0,
  "next_action": "await_response" | "send_return_address" | "escalate",
  "alert_type": null
}

BESLISLOGICA:
- "threat" → "escalated", ${ladderCfg.dreiging_percentage}%.
- "compensation"/"return":
  * Bied ${stepPercent}% (HTML, empathisch, geen “geld terug” of “retour”).
  * Als klant hoger % vraagt en ≤ stap+${maxExtra}% en ≤ ${ladderCfg.max_totaal_normaal}% (of dreiging ≤ ${ladderCfg.max_totaal_dreiging}%) → geef dat percentage terug.
  * Akkoord → status "accepted", alert_type "accepted".
  * Duidelijke weigering op max-stap of "stuur retouradres" → status "max_refused", next_action "send_return_address", alert_type "max_refused".
- "delivery":
  * Pas venster toe; bij >${deliveryCfg.drempel_2} dagen → alert_type "late_delivery".
- "cancellation" (binnen ${annuleerMax} dagen) → status "processed", alert_type "cancellation_request".
- "address_change" (binnen ${adresMax} dagen) → status "processed", alert_type "address_change_request".
- "confirmation_missing" → kalmeren + spamcheck; alert_type "confirmation_missing".
- "change_order_request" → beleefd aankondigen dat we dit checken; alert_type "change_order_request".
- "general": kort antwoord.
`.trim();

if (isThreat) {
  prompt += `

DREIGING-DETECTIE: Dit bericht bevat dreigingswoorden. Neem dit mee.`;
}

return [{ json: { chatInput: prompt } }];

function safeParse(x){ if (typeof x !== 'string') return x; try{ return JSON.parse(x); } catch{ return {}; } }

const raw = $node["Message a model1"].json;
const content = raw?.choices?.[0]?.message?.content;
if (!content) throw new Error("Geen AI-payload!");
const payload = (typeof content === "object") ? content : safeParse(content);

// Thread/DB
const upsert = $node["Conversation Thread Upsert"].json || {};
const thread_id = upsert.thread_id || $node["Thread ID Generator"].json.thread_id;
const dbTotal   = (typeof upsert.total_interactions === 'number') ? upsert.total_interactions : null;
const genCount  = $node["Thread ID Generator"].json.contact_count || 1;
const contact_count = dbTotal ?? genCount;

// Tenant/signature
const tenant = $node["Get tenant data"].json || {};
const persona = tenant.ai_persona_name || 'Eva';
const bedrijf = tenant.bedrijfsnaam || '';
const signature = `<br>Met vriendelijke groet,<br>${persona}${bedrijf ? ' – ' + bedrijf : ''}`;

// Response + signoff dedupe
let ai_response = String(payload.ai_response || "");

// Voeg retouradres toe bij max_refused
if ((payload.status || '').toLowerCase() === "max_refused") {
  const addr = tenant.retouradres || "";
  if (addr) {
    ai_response += `<p>Het retouradres is:<br>${addr}.<br>Let op: de retourkosten zijn voor de klant.</p>`;
  }
}

// voorkom dubbele sign-off
const norm = ai_response.replace(/\s+/g,' ').toLowerCase();
const normSig = signature.replace(/\s+/g,' ').toLowerCase();
if (!norm.includes(normSig)) ai_response += signature;

// type/next_action/alert
let status = (payload.status || "processed").toLowerCase();
let type   = (payload.type   || "general").toLowerCase();
if (payload.compensatie_percentage != null && (type === "return" || type === "general")) type = "compensation";

let nextAction = payload.next_action || "await_response";
if (type === "threat") nextAction = "escalate";
if (status === "max_refused") nextAction = "send_return_address";
if (status === "accepted") nextAction = "await_response";

return [{
  json: {
    ai_response,
    status,
    type,
    compensatie_percentage: payload.compensatie_percentage ?? null,
    ladder_stap: payload.ladder_stap ?? 1,
    escalation_reason: payload.escalation_reason ?? null,
    mood_detected: payload.mood_detected ?? null,
    confidence_score: payload.confidence_score ?? 0,
    alert_type: payload.alert_type ?? null,

    next_action: nextAction,
    thread_id,
    contact_count,
    tenant_id: tenant.tenant_id,
    original_email: $node["Email body cleaner"].json
  }
}];

