function toText(html){ return String(html||'').replace(/<[^>]+>/g,' ').replace(/\s{2,}/g,' ').trim(); }
function lower(x){ return String(x||'').toLowerCase(); }

const email   = $node["Email body cleaner"].json || {};
const tenant  = $node["Get tenant data"].json || {};
let history   = Array.isArray($node["Get Conversation History"].json) ? $node["Get Conversation History"].json : [];
const lookup  = $node["Conversation Thread Lookup"].json || [];
const gen     = $node["Thread ID Generator"].json || [];

const rulesList = tenant.business_rules || [];
const rules = Object.fromEntries(rulesList.map(r => [r.rule_key, r.rule_config || {}]));

const locale = tenant.locale?.split('-')[0] || 'nl';

// ======== Klantnaam uit ondertekening (multi-language)
let klantnaam = email.detected_signature_name || email.customer_name || "";
if (!klantnaam) klantnaam = (email.customer_email || "").split("@")[0];

// ======== Mood detectie
const negativeWordsPerTaal = {
  nl: ["niet blij", "ontevreden", "slecht", "teleurgesteld", "waardeloos", "lang wachten", "super lang", "erg langzaam", "niet tevreden", "onvoldoende", "te weinig", "jammer", "helaas", "ben niet blij", "onacceptabel", "onprettig", "geen zin", "klaar mee", "vreselijk"],
  en: ["not happy", "dissatisfied", "bad", "disappointed", "poor", "waiting too long", "super long", "very slow", "unhappy", "insufficient", "unfortunately"],
  de: ["nicht zufrieden", "schlecht", "enttäuscht", "wartet zu lange", "ärgerlich", "nicht genug", "leider"]
};
const body = lower(email.cleaned_body || email.email_body || "");
const negativeWords = negativeWordsPerTaal[locale] || negativeWordsPerTaal.nl;
const mood = negativeWords.some(w => body.includes(w)) ? "negative" : "neutral";

// ======== Business rule configs
const ladderRaw = rules.compensatie_ladder || {};
const ladderCfg = {
  stappen: ladderRaw.stappen || [15, 20, 30, 40],
  dreiging_percentage: ladderRaw.dreiging_percentage ?? ladderRaw.dreiging ?? 50,
  onderhandeling_marge: ladderRaw.onderhandeling_marge ?? ladderRaw.max_extra_percentage ?? 15,
  max_totaal_normaal: ladderRaw.max_totaal_normaal ?? ladderRaw.max_normaal ?? 40,
  max_totaal_dreiging: ladderRaw.max_totaal_dreiging ?? ladderRaw.max_dreiging_totaal ?? 65,
  retour_na_weigering: ladderRaw.retour_na_weigering ?? true
};
const weigering = rules.weigering_detectie || { triggers:['nee','niet akkoord','ik wil retourneren','stuur adres','retour adres','ik wil terugsturen','onvoldoende','te weinig','helaas niet','jammer'], alleen_binnen_categorie:true };
const deliveryCfg = rules.levering_logica   || { drempel_1:5, drempel_2:9, normale_levertijd_werkdagen:'6-9' };

const annuleerMax = tenant.annulering_toegestaan_dagen ?? (rules.annulering_policy?.max_dagen_na_bestelling ?? 14);
const adresMax    = tenant.adreswijziging_toegestaan_dagen ?? rules.adreswijziging_policy?.max_dagen_na_bestelling ?? 7;
const retourAdres = tenant.retouradres || "";

// ======== Slimme ladder/teller — **DIT IS DE FIX**
let refusal_count = 0;
if (history.length > 0) {
  for (let h of history) {
    if (
      ['compensation', 'return'].includes((h.type||'').toLowerCase()) &&
      (h.refusal_detected === true || (typeof h.refusal_detected === 'string' && h.refusal_detected.toLowerCase() === 'true'))
    ) {
      refusal_count += 1;
    }
  }
}
let ladder_stap = 1 + refusal_count;

// Herken weigering in huidige klantbericht
const refusalHit = (weigering.triggers || []).some(t => body.includes(lower(t)));
let aiRefusalDetected = false;
if (refusalHit) {
  aiRefusalDetected = true;
  ladder_stap += 1; // **Extra stap direct omhoog**
}

// Cap op max
if (ladder_stap > ladderCfg.stappen.length) ladder_stap = ladderCfg.stappen.length;
const stepPercent = ladderCfg.stappen[ladder_stap-1] ?? ladderCfg.stappen[0];

// ======== Dagen-detectie (uit tekst of history)
function detectDaysSinceOrder(text) {
  let days = null;
  if (/gisteren|yesterday/i.test(text)) days = 1;
  else if (/eergisteren|the day before yesterday/i.test(text)) days = 2;
  else if (/vorige week|last week/i.test(text)) days = 7;
  else if (/(\d+) dagen? geleden|(\d+) days? ago/i.test(text)) {
    const match = text.match(/(\d+)\s*dagen? geleden|(\d+)\s*days? ago/i);
    days = parseInt(match[1] || match[2], 10);
  }
  return days;
}
const daysFromText = detectDaysSinceOrder(body);
function daysSinceFirstContact(){
  if (!history.length) return null;
  const oldest = history[history.length-1];
  try { return Math.floor((Date.now() - new Date(oldest.created_at).getTime()) / (1000*60*60*24)); }
  catch { return null; }
}
const daysFromHistory = daysSinceFirstContact();
const days = daysFromText ?? daysFromHistory;

// ======== Dreiging?
const threatWords = (rules.dreiging_detectie?.trefwoorden || []).map(lower);
const isThreat = threatWords.some(kw => body.includes(kw));

// ======== Volledige conversatie (voor logging/metadata & AI-context)
const conversation = history.slice(0, 5).map((h, i) => ({
  nr: i + 1,
  klant: toText(h.message_body || ''),
  ai: toText(h.ai_response  || ''),
  type: (h.type || '').toLowerCase(),
  ladder_stap: h.ladder_stap ?? null,
  refusal_detected: h.refusal_detected ?? false,
  created_at: h.created_at || null,
}));

// ======== AI-prompt (met alles wat je wilt, zie je eigen instructies)
let prompt = `
**LET OP:**
- JE MAG NOOIT de woorden 'retour', 'retourneren', 'geld terug', 'terugsturen' of het retouradres benoemen, tenzij klant weigert op de hoogste compensatiestap (max_ladder). Alleen DAN mag je het retouradres geven!
- In alle andere gevallen: bied alleen een compensatiepercentage aan, focus op het behouden van het product bij de klant.
- Alleen bij status "max_refused" mag je het retouradres sturen, vermeld: 'De retourkosten zijn voor de klant. Zie retourbeleid op de website.'

Je bent ${tenant.ai_persona_name || 'Eva'}, professionele klantenservice van ${tenant.bedrijfsnaam || 'onze winkel'}. Schrijf menselijk, natuurlijk en **VARIEER altijd je zinnen** (geen standaardantwoorden), wees vriendelijk en empathisch, en pas je toon aan op basis van de emotie/mood van de klant.

**INSTRUCTIES:**
- Begrijp altijd het bericht en de frustratie of wens van de klant (noem deze expliciet).
- Bied **precies het juiste percentage van de ladder** (${ladderCfg.stappen.join(', ')}) aan bij elke weigering, zonder te verwijzen naar contactmomenten of 'eerste/volgende aanbieding'.
- Elke volgende weigering = **volgende stap in ladder**.
- **Nooit** klant vragen: “Wat wilt u zelf?” of “Wat zou u passend vinden?”; AI biedt altijd zelf iets aan.
- Variatie verplicht: andere openingszinnen, nooit dubbele sign-off, nooit dezelfde bewoording.
- Nooit over retour of geld terug praten, tenzij max ladder-stap geweigerd!
- Toon altijd begrip, en maak het aanbod persoonlijk (“We hopen dat deze tegemoetkoming het voor je goed maakt.”)
- Als klant akkoord: status “accepted”, als hoogste stap geweigerd: status “max_refused” (retouradres sturen).
- Gebruik ALLE context en history (zie hieronder) voor je antwoord.
- Voeg altijd een JSON-output toe zoals hieronder:

**GESPREKSGESCHIEDENIS:** (nieuwste boven)
${conversation.length ? conversation.map(c => 
`[${c.created_at || ''}] KLANT: "${c.klant}" | AI: "${c.ai}"${c.ladder_stap ? ' (ladder:' + c.ladder_stap + ')' : ''}`
).join('\n') : 'Geen eerdere gesprekken.'}

**HUIDIGE KLANT:**
Naam: ${klantnaam}
Onderwerp: ${email.subject || ""}
Bericht: ${email.cleaned_body || email.email_body}
Mood (AI-detectie): ${mood}

**CONTACT:**
- Ladder-stap te hanteren: ${ladder_stap} ⇒ ${stepPercent}% (tenzij onderhandeling expliciet hoger vraagt)
- Dagen sinds bestelling (geschat): ${days !== null ? days : "onbekend"}

**OUTPUT (1 JSON object):**
{
  "ai_response": "<p>...</p>",
  "status": "processed" | "escalated" | "max_refused" | "accepted",
  "type": "general" | "delivery" | "cancellation" | "address_change" | "compensation" | "return" | "threat" | "confirmation_missing" | "change_order_request",
  "compensatie_percentage": ${stepPercent},
  "ladder_stap": ${ladder_stap},
  "mood_detected": "${mood}",
  "refusal_detected": ${aiRefusalDetected},
  "interpretation": ""
}
`;

// ======== OUTPUT: ALLES incl. conversation array voor metadata!
return [{
  json: { 
    chatInput: prompt, 
    mood_detected: mood, 
    klantnaam, 
    ladder_stap,
    stepPercent,
    aiRefusalFallback: aiRefusalDetected, 
    days,
    conversation 
  }
}];

function safeParse(x){ if (typeof x !== 'string') return x; try{ return JSON.parse(x); } catch{ return {}; } }

const raw = $node["Message a model1"].json;
const content = raw?.choices?.[0]?.message?.content;
if (!content) throw new Error("Geen AI-payload!");
const payload = (typeof content === "object") ? content : safeParse(content);

const contextBuilder = $node["AI Context Builder"].json || {};
const fallbackRefusal = contextBuilder.aiRefusalFallback === true;
const days = contextBuilder.days ?? null;
const conversation = contextBuilder.conversation || [];  // <--- Toevoegen!

// Thread/DB
const upsert = $node["Conversation Thread Upsert"].json || {};
const thread_id = upsert.thread_id || $node["Thread ID Generator"].json.thread_id;
const dbTotal   = (typeof upsert.total_interactions === 'number') ? upsert.total_interactions : null;
const genCount  = $node["Thread ID Generator"].json.contact_count || 1;
const contact_count = dbTotal ?? genCount;

// Tenant/signature
const tenant = $node["Get tenant data"].json || {};
const persona = tenant.ai_persona_name || 'Eva';
const bedrijf = tenant.bedrijfsnaam || '';
const signature = `<br>Met vriendelijke groet,<br>${persona}${bedrijf ? ' – ' + bedrijf : ''}`;

let ai_response = String(payload.ai_response || "");

// Voeg retouradres toe bij max_refused
if ((payload.status || '').toLowerCase() === "max_refused") {
  const addr = tenant.retouradres || "";
  if (addr) {
    ai_response += `<p>Het retouradres is:<br>${addr}.<br>Let op: de retourkosten zijn voor de klant.</p>`;
  }
}

// voorkom dubbele sign-off
const norm = ai_response.replace(/\s+/g,' ').toLowerCase();
const normSig = signature.replace(/\s+/g,' ').toLowerCase();
if (!norm.includes(normSig)) ai_response += signature;

// refusal_detected: AI is leidend, maar keyword fallback vangt alles op!
let refusal_detected = payload.refusal_detected === true || fallbackRefusal;

return [{
  json: {
    ai_response,
    status: (payload.status || "processed").toLowerCase(),
    type: (payload.type   || "general").toLowerCase(),
    compensatie_percentage: payload.compensatie_percentage ?? null,
    ladder_stap: payload.ladder_stap ?? 1,
    mood_detected: payload.mood_detected ?? null,
    refusal_detected,
    interpretation: payload.interpretation || "",
    days,
    confidence_score: payload.confidence_score ?? 0,
    alert_type: payload.alert_type ?? null,
    next_action: payload.next_action || "await_response",
    thread_id,
    contact_count,
    tenant_id: tenant.tenant_id,
    klantnaam: payload.klantnaam || contextBuilder.klantnaam || null,
    conversation, // <-- Belangrijk!
    original_email: $node["Email body cleaner"].json
  }
}];

