// Parse inkomende Gmail-trigger naar bruikbare velden (+ naam uit From)
const email = $input.first().json;

const fromHeader  = email.From || email.from || "";
const toHeader    = email.To || email.to || "";
const nameMatch   = fromHeader.match(/^"?([^"<]+?)"?\s*<.+>$/);
const customer_name = (nameMatch && nameMatch[1]) ? nameMatch[1].trim() : null;

const match       = fromHeader.match(/<(.+)>/);
const customer_email = match ? match[1] : String(fromHeader || "").trim();

const email_body = email.textHtml || email.textPlain || email.snippet || "";
const subject    = (email.Subject || email.subject || email.snippet || "Geen onderwerp").toString().trim();
const tenant_email = toHeader || "";
const email_id   = email.id || email.internalDate;
const received_at = new Date().toISOString();

return [{
  json: {
    tenant_email,
    customer_email,
    customer_name,
    email_body,
    subject,
    email_id,
    received_at
  }
}];

function uuidv4(){
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, c=>{
    const r = Math.random()*16|0, v = c==='x'? r : (r&0x3|0x8);
    return v.toString(16);
  });
}

const lookup = $node["Conversation Thread Lookup"].json;
let thread_id, is_new_thread, contact_count;

if (Array.isArray(lookup) && lookup[0]?.thread_id) {
  thread_id = lookup[0].thread_id;
  const total = Number(lookup[0].total_interactions || 0);
  contact_count = total + 1;   // volgende contact
  is_new_thread = false;
} else {
  thread_id = uuidv4();
  contact_count = 1;
  is_new_thread = true;
}

return [{ json: { thread_id, is_new_thread, contact_count } }];

let input = $node["Email parser"]?.json;
if (!input) throw new Error("Email parser heeft geen output!");
let html = input.email_body || "";

// Quotes/Gmail reply blokken strippen
html = html
  .replace(/<blockquote[\s\S]*?<\/blockquote>/gi, "")
  .replace(/<div class="gmail_quote"[\s\S]*?$/gi, "");

// "On ... wrote/schreef:"
html = html.replace(/On .* (wrote|schreef):[\s\S]*/i, "");

// HTML → plain text, quotes weg
let text = html
  .replace(/<style[\s\S]*?<\/style>/gi, "")
  .replace(/<script[\s\S]*?<\/script>/gi, "")
  .replace(/<[^>]+>/g, " ")
  .replace(/^[>].*$/gmi, "")
  .replace(/\s{2,}/g, " ")
  .trim();

for (const r of [
  /-{2,}\s*Original Message\s*-{2,}/i,
  /-{2,}\s*Oorspronkelijk bericht\s*-{2,}/i
]) { if (r.test(text)) { text = text.split(r)[0].trim(); break; } }

return [{
  json: {
    ...input,
    cleaned_body: text
  }
}];
